class ActionCard{
  constructor(JSONCard) {
    this.id = JSONCard.id;
    this.name = JSONCard.name;
    this.img = JSONCard.img;
    this.desc = JSONCard.desc;
    this.acttext = JSONCard.acttext;
    this.target = JSONCard.target;
    this.location = JSONCard.location;
    this.isPlayer = false;
  }


  getID(){
    return this.id;
  }

  getName(){
    return this.name;
  }

  getIMG(){
    return this.img;
  }

  getDesc(){
    return this.desc;
  }

  getLoc(){
    return this.location;
  }

  setLoc(newLoc){
    this.location = newLoc;
  }

  getAction(){
    return this.acttext;
  }

  getTarget(){
    return this.target;
  }

//  playActionCard(target, godDeck){
  //	target.infamy +=1;
  //	target.glory +=1;
  //	godDeck.infamy -=1;
  //	godDeck.glory -=1;
  //  updateGI();
  //  hideTargets();
  //  this.location = "Deck";
    //this.isPlayer = true;
    //reverseCard("player1Card1");
//  }

  identifyTargets(GodTiles){

    var targets = [];

      if(this.id === 1 || this.id === 4){
        for(var i = 0; i < GodTiles.length; i++){
          if((GodTiles[i].getGlory() > 0) && (GodTiles[i].getLoc() != this.getLoc()))
            targets.push(GodTiles[i]);
          }

          if(this.id === 4 && targets.length < 2)
            targets = [];

          return targets;
        }

      else if(this.id === 2 || this.id === 9){
        for(var i = 0; i < GodTiles.length; i++){
          if((GodTiles[i].getInfamy() > 0) && (GodTiles[i].getLoc() != this.getLoc()))
            targets.push(GodTiles[i]);
          }

          if(this.id === 2 && targets.length < 2)
            targets = [];

          return targets;
        }

      else if(this.id === 3 || this.id === 5 || this.id === 7 || this.id === 8 || this.id === 10 || this.id === 15 || this.id === 16){
        for(var i = 0; i < GodTiles.length; i++){
          if((GodTiles[i].getLoc() != this.getLoc()))
            targets.push(GodTiles[i]);
          }
          return targets;
        }


      else if(this.id === 6){ //need to add more specific cases
        var gloryTargets = [];
        var infamyTargets = [];
        for(var i = 0; i < GodTiles.length; i++){
          if((GodTiles[i].getGlory() > 0) || (GodTiles[i].getInfamy() > 0))
              targets.push(GodTiles[i]);
          }


        for(var i = 0 ; i < targets.length; i++){
          if(targets[i].getGlory > 0)
            gloryTargets.push(targets[i]);
          if(targets[i].getInfamy > 0)
            infamyTargets.push(targets[i]);
        }

          if(targets.length < 1 || gloryTargets.length < 0 || infamyTargets.length < 0)
            targets = [];
        return targets;
      }

      else if(this.id === 11 || this.id === 13){
        for(var i = 0; i < GodTiles.length; i++){
          if(GodTiles[i].getLoc() === this.getLoc() && (GodTiles[i].getGlory() > 0) && GodTiles[i].getInfamy() > 0)
            targets.push(GodTiles[i]);
          }

          return targets;
        }

      else if(this.id === 12){
        for(var i = 0; i < GodTiles.length; i++){
          if((GodTiles[i].getLoc() === this.getLoc() && GodTiles[i].getIsUsed()))
            targets.push(GodTiles[i]);
          }
          return targets;
        }

      else if(this.id === 14)
        return GodTiles;

      else
        return targets;
    }

    playActionCard3(Origin, gloryTarget, infamyTarget, Deck){
        if(this.id === 11){
          Origin.setInfamy(Origin.getInfamy()-1);
          Origin.setGlory(Origin.getGlory()-1);
          gloryTarget.setGlory(gloryTarget.getGlory()+1);
          infamyTarget.setInfamy(infamyTarget.setInfamy()+1);
        }
    }

    playActionCard2(Origin, Destination, Deck){
      if(this.id === 6){
        //intial is glory, destination is infamy
        Origin.setGlory(Origin.getGlory()+1); //gains Glory
        Origin.setInfamy(Origin.getInfamy()-1); //loses Infamy
        Destination.setGlory(Destination.getGlory()-1); //loses glory
        Destination.setInfamy(Destination.getInfamy()+1); //gains Infamy
      }

      else if(this.id === 2){
        Origin.setInfamy(Origin.getInfamy()-1);
        Destination.setInfamy(Destination.getInfamy()+1);
      }

      else if(this.id === 4){
        Origin.setGlory(Origin.getGlory()-1);
        Destination.setGlory(Destination.getGlory()+1);
      }
    }

    playActionCard(Destination, Deck){
      switch(this.id){
        case 1:
          Destination.setGlory(Destination.getGlory()-1);
          Deck.glory = Deck.glory+1;
          break;
        case 3:
          Destination.setInfamy(Destination.getInfamy()+2);
          Deck.infamy = Deck.infamy-2;
          break;
        case 5:
          Destination.setGlory(Destination.getGlory()+2);
          Deck.glory= Deck.glory-2;
          break;
        case 7:
          Destination.setGlory(Destination.getGlory()+1);
          Destination.setInfamy(Destination.getInfamy()+1);
          Deck.infamy = Deck.infamy-1;
          Deck.glory = Deck.glory-1;
          break;
        case 8:
          Destination.setGlory(Destination.getGlory()+1);
          Deck.glory = Deck.glory - 1;
          break;
        case 9:
          Destination.setInfamy(Destination.getInfamy()-1);
          Deck.infamy = Deck.infamy+1;
          break;
        case 10:
          Destination.setInfamy(Destination.getInfamy()+1);
          Deck.infamy = Deck.infamy - 1;
          break;
        case 12:
          Destination.setIsUsed();
          Destination.setInfamy(Destination.getInfamy()+2);
          break;
        case 13:
          Destination.setGlory(Destination.getGlory()-1);
          Destination.setInfamy(Destination.getInfamy()-1);
          Deck.glory = Deck.glory + 1;
          Deck.infamy = Deck.infamy + 1;
          break;
        case 14:
          if(Deck.glory > Deck.infamy){
            Destination.setInfamy(Destination.getInfamy()+1);
            Deck.infamy = Deck.infamy-1;
          }
          else{
            Destination.setGlory(Destination.getGlory()+1);
            Deck.glory = Deck.glory - 1;
          }
          break;
        case 15:
          Destination.setGlory(Destination.getGlory()+2);
          Destination.setInfamy(Destination.getInfamy()+1);
          Deck.glory = Deck.glory - 2;
          Deck.infamy = Deck.infamy - 1;
          break;
        case 16:
          Destination.setGlory(Destination.getGlory()+1);
          Destination.setInfamy(Destination.getInfamy()+2);
          Deck.infamy = Deck.infamy - 2;
          Deck.glory = Deck.glory - 1;
          break;
        default:
          break;
      }
    }
}
