class GodTile{
  constructor(JSONCard) {
    this.id = JSONCard.id;
    this.name = JSONCard.name;
    this.img = JSONCard.img;
    this.desc = JSONCard.desc;
    this.location = JSONCard.location;
      this.glory = 1;
      this.infamy = 2;
      this.IsUsed = false;
     }

  getName(){
    return this.name;
  }

  getID(){
    return this.id;
  }

  getImg(){
    return this.name;
  }

  getDesc(){
    return this.desc;
  }

  getLoc(){
    return this.location;
  }

  setLoc(loc){
    this.location = loc;
  }

  getGlory(){
    return this.glory;
  }

  setGlory(newGlo){
    if(this.id === 8 && this.getIsUsed())
      return;
    this.glory = newGlo;
  }

  getScore(){
    return this.glory*2-this.infamy;
  }

  getInfamy(){
    return this.infamy;
  }

  setInfamy(newInf){
    if(this.id === 8 && this.getIsUsed())
      return;
    this.infamy = newInf;
  }

  getIsUsed(){
    return this.IsUsed;
  }

  setIsUsed(){
    if(this.id === 8 && this.getIsUsed())
      return;
    this.IsUsed = !this.IsUsed;
    if(this.IsUsed) //if the God Tile has been used
      this.setInfamy(this.Infamy++);
    else //if the God Tile has been set back to False
      this.setInfamy(this.Infamy--);
  }

  getLoc(){
    return this.location;
  }

  pickRandom(range){
    return Math.floor(Math.random()*range);
  }

  playTile(Phase,inGameTiles,Players,Deck){ //may very likely have to pass in an instance of Game
    if(this.getIsUsed())
      return;
    switch(Phase){

      case "Start": //PRAXIS
          this.setIsUsed();
          return;

      case "Before Player Action":
        if(this.id === 1){ //ANATOC
          var playerHands = new Deck("none");

          for(var i = 0; i < Players.length; i++){ //pushing all cards onto deck
            var removed = Players[i].removeHand();
            for(var j = 0; j < removed.length; j++)
              playerHands.deck.push(removed[j]);
          }
          playerHands.shuffle(); //shuffling all of their hands
          playerHands.deal(3, Players,"ActionCards");
          this.setIsUsed();
        }

        else if(this.id === 5){//ENDYMINA
          for(var i = 0; i < Players.length; i++){
            if(this.getLoc() === Player[i].getUsername() && !Player[i].getTurn()){
                Player[i].addActionCard(Deck.getCard(0));
                Player[i].addActionCard(Deck.getCard(0));



                //to be changed once finalize user choices
                var chosenCard1 = Player[i].getHand()[pickRandom(Player[i].getHand().length)];
                //rest of code should be good



                var removedCard = Player[i].removeCard(chosenCard.getID());
                console.log("Removed Card:", removedCard)
                Player[i].addActionCard(chosenCard1);

                //to be changed once finalize user choices
                var chosenCard2 = Player[i].getHand()[pickRandom(Player[i].getHand().length)];
                //rest of code should be good

                removedCard = Player[i].removeCard(chosenCard.getID());
                console.log("Removed Card:", removedCard)
                Player[i].addActionCard(chosenCard2);
                this.setIsUsed();
                return;
            }
          }
        }
        break;
      case "Renewal":
        if(this.id === 6){//NANOS, IMPLEMENTED IN GAME LOOP
          this.setIsUsed();
        }
        else if(this.id === 4){//CYMELE
          for(var i = 0; i < Players.length; i++){
            if(this.getLoc() === Players[i].getUsername()){
              var toDisplay = [];

              for(var i = 0; i < 4; i++)
                toDisplay.push(Deck.getIndex(0));

              //display cards somehow, this part will be changed once we figure out how to display and log user choice
              var chosenCard = toDisplay[pickRandom(4)];
              //end of unfinalized code

              Player[i].addActionCard(chosenCard);
              for(var j = 0; j < toDisplay.length; j++){
                Deck.deck.push(toDisplay[0])
                toDisplay = toDisplay.filter(item => item !== toDisplay[0]);
              }
              this.setIsUsed();
              return;
            }
          }
        }
        break;
      default:
        if(this.id === 2){//ARISTIPHANE
          var nextTile, chosenTile;

          //have player pick two godTiles from godTiles
          chosenTile = inGameTiles[pickRandom(inGameTiles.length)];
          do{
            nextTile = inGameTiles[pickRandom(inGameTiles.length)];
          }
          while(nextTile === chosenTile)
          //end of user Selection

          //swapping Glory
          var temp = nextTile.getGlory();
          nextTile.setGlory(chosenTile.getGlory());
          chosenTile.setGlory(temp);
          //swapping Infamy
          var temp = nextTile.getInfamy();
          nextTile.setInfamy(chosenTile.getInfamy());
          chosenTile.setInfamy(temp);
          this.setIsUsed();
          return;
        }
      else if(this.id === 3){//CASSANA
        var targets = [];
        for(var i = 0; i < inGameTiles.length; i++){
          if(inGameTiles[i].getGlory() > 4)
            targets.push(inGameTiles[i]);
          }
        //display targets, this block is subject to change
        if(targets.length != 0){

          //picking random choice
          var randomTarget = pickRandom(targets.length);
          //end of random choice

          targets[randomTarget].setGlory(targets[randomTarget].getGlory+1);
          this.setIsUsed();
        }
        return;
      }
     else if(this.id === 7){//PITON
       var targets = [];
       for(var i = 0; i < inGameTiles.length; i++){
         if(!inGameTiles[i].getIsUsed())
           targets.push(inGameTiles[i]);
         }
       if(targets.length != 0){

         //picking random choice, this should be the player's choice
          var randomTarget = pickRandom(targets.length);
          //end of to be changed code

          console.log("chose to use",targets[randomTarget].getName()+"\'s power");
          this.id = targets[randomTarget].getID();
          this.playTile(Phase,inGameTiles,Players,Deck) //recursive call with new id to use power of chosen Tile
          this.id = 7;
         }
           return;
     }
     else if(this.id === 9){//RYMON
       //need to do a check within the game in order to call the method, will have to add buzzer for user at the moment in which actionCard is Played
     }
        break;
    }
  }
}
